import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { setMovies } from "../store/movies";

const Movies = ({ ArraySlicer }) => {
  let { Search } = useSelector((state) => state.movies);
  Search = ArraySlicer(Search);

  return (
    <div className="moviesAndButtonsDiv">
      <div className="moviesMainDiv">
        {Search &&
          Search.map((movies) => {
            return (
              <div className="movieByFiveDiv">
                {movies.map((movie) => {
                  return (
                    <div className="movieRowDiv">
                      <Link
                        className="styledLink"
                        to={`/movies/${movie.imdbID}`}
                      >
                        <h1>{movie.Title}</h1>
                        <img src={movie.Poster}></img>
                      </Link>
                    </div>
                  );
                })}
              </div>
            );
          })}
        {!Search.length ? (
          <div className="moviesAreEmptyMainDiv">
            <div className="moviesAreEmptyDiv">
              <h1>No movies loaded.</h1>
              <Link to="/">
                <button className="submitButton">Search For a Movie?</button>
              </Link>
            </div>
          </div>
        ) : null}
      </div>
      <div className="nextPageButtonDiv">
        <button>{`>>`}</button>
      </div>
    </div>
  );
};

export default Movies;
