import React from "react";
import Movies from "../components/Movies";
import ArraySlicer from "../hooks/ArraySlicer";
import { useDispatch, useSelector } from "react-redux";
import { setPaginationCounter } from "../store/paginationCounter";

const MoviesContainer = () => {
  const { paginationCounter } = useSelector((state) => state);

    useEffect(() => {
    console.log("HOLAAAAA");
    // axios
    //   .get(`https://www.omdbapi.com/?${s}${y}${type}${page}&apikey=62e1844`)
    //   .then((res) => res.data)
    //   .then((movies) => {
    //     dispatch(setMovies(movies));
    //   });
  }, [paginationCounter]);

  const dispatch = useDispatch();

  const clickHandler = (e) => {
    const { name } = e.target;
    dispatch(
      setPaginationCounter(
        name === "nextPage"
          ? paginationCounter + 1
          : paginationCounter - 1
      )
    );
  };
  return <Movies ArraySlicer={ArraySlicer} clickHandler={clickHandler} />;
};

export default MoviesContainer;
