import React from "react";
import SingleMovie from "../components/SingleMovie";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

// https://www.omdbapi.com/?i=tt0096895&apikey=62e1844

const SingleMovieContainer = ({ match }) => {
  const { validatedUser } = useSelector((store) => store);

  const clickHandler = (movieId) => {
    const userId = validatedUser.data.user.id;
    axios
      .post("/api/favorites", { userId, movieId })
      .then((data) => console.log("DATAAAAAA", data));
  };

  return <SingleMovie match={match} clickHandler={clickHandler} />;
};

export default SingleMovieContainer;
