import React from 'react'
import ArrayPromisifier from '../hooks/ArrayPromisifier'
import { useEffect } from 'react'

const SingleUserInfo = ({singleUser}) => {

    const [singleUserMovies, setSingleUserMovies] = useState([])

    console.log(singleUser)

    const { favorites } = singleUser

    let promis = ArrayPromisifier(favorites)

    console.log(promis)

    useEffect(() => {
        promis = ArrayPromisifier(favorites)
    }, [favorites])

    console.log('MOVIEEEEESSSSSSSS', promis.then((result) => result))

    return (
    <div className='singleUserMainDiv'>
        <h1 className='singleUserTitle'>{singleUser.username}</h1>
        <h2>Favorite Movies:</h2>
        <div className='favoriteMoviesFromSingleUserDiv'>
        {favorites.map((favorite) => {
            return <h1 className='singleUserFavoriteMovieTitle'>{favorite.movieId}</h1>
        })}
        </div>
    </div>
    )
}

export default SingleUserInfo;
