import React from "react";
import SingleUser from "../components/SingleUser";
import { useSelector, useDispatch } from "react-redux";
import { setSearchUser } from "../store/searchUser";
import { setSingleUser } from "../store/singleUser";
import { useEffect } from 'react'
import axios from "axios";

const SearchSingleUser = () => {

  const [userValidated, setUserValidated] = useState(false)

  const { searchUser } = useSelector((store) => store);
  const dispatch = useDispatch();

  const { validatedUser } = useSelector((store) => store);

  useEffect(() => {
    axios
      .get(`/api/auth/users`, {
        headers: { authorization: `Bearer ${validatedUser.token}` },
      })
      .then((res) => {
        setUserValidated(true)
        console.log('USERRRRRRRRSEARCH', res.data)
      })
      .catch((err) => {
        setUserValidated(false)
        console.log('ERRRRRRRRR', err)
      })
  }, []);

  const changeHandler = (e) => {
    e.preventDefault();
    let key = e.target.name;
    let { value } = e.target;
    dispatch(setSearchUser({ [key]: value }));
  };

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .get(`/api/users/${searchUser.username}`)
      .then((res) => dispatch(setSingleUser(res.data)));
  };

  return <SingleUser changeHandler={changeHandler} submitHandler={submitHandler} userValidated={userValidated}/>;
};

export default SearchSingleUser;
