import React from "react";
import Favorites from "../components/Favorites";
import { useEffect } from "react";
import { useSelector, useDispatch } from "react-redux";
import { setFavorites } from "../store/userFavorites";
import axios from "axios";

const FavoritesContainer = () => {
  const { validatedUser } = useSelector((store) => store);
  const { favorites } = useSelector((store) => store);

  const dispatch = useDispatch();

  const userId = validatedUser.data.user.id;

  // https://www.omdbapi.com/?i=tt0096895&apikey=62e1844

  useEffect(() => {
    axios.get(`/api/favorites/${userId}`).then((userFavorites) => {
      let { favorites } = userFavorites.data
      console.log("USERFAVORITESSSSS", favorites);
      favorites = favorites.map((favoritePromise, index) => {
        return axios.get(`https://www.omdbapi.com/?i=${favoritePromise.movieId}&apikey=62e1844`)
      })
      // console.log('PROMISIFIED FAVORITES', favorites)
      Promise.all(favorites).then((values) => console.log('VALUESSSSS', values))
      dispatch(setFavorites(favorites));
    });
  }, []);
  //FAVORITES ESTA EXPLOTANDO
  console.log("!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!", favorites);

  return <Favorites />;
};

export default FavoritesContainer;
