import React from "react";
import axios from "axios";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setSingleMovie } from "../store/singleMovie";
import { Link } from "react-router-dom";

const SingleMovie = ({ match }) => {
  const { singleMovie } = useSelector((state) => state);
  const dispatch = useDispatch();
  const { id } = match.params;

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?i=${id}&apikey=62e1844`)
      .then((res) => res.data)
      .then((singleMovie) => {
        dispatch(setSingleMovie(singleMovie));
      });
  }, []);

  const {
    Title,
    Director,
    Actors,
    Plot,
    Poster,
    Country,
    Writer,
    Released,
    Genre,
    Ratings,
  } = singleMovie;

  const technicalStuff = [Director, Actors, Writer, Released, Country, Genre];
  const stuff = [
    "Director",
    "Actors",
    "Writer",
    "Released",
    "Country",
    "Genre",
  ];

  console.log("SINGLEMOVIEEEEE", singleMovie);

  return (
    <div className="singleMovieContainer">
      <div className="singleMovieCastTitleAndPosterDiv">
        <div>
          {technicalStuff.map((item, index) => {
            return (
              <h3 className="singleMovieCastDiv">
                {stuff[index]}: {item}
              </h3>
            );
          })}
          {/* <h3 className="singleMovieCastDiv">Director: {Director}</h3> */}
          {/* <h3 className="singleMovieCastDiv">Cast: {Actors}</h3>
          <h3 className="singleMovieCastDiv">Writer: {Writer}</h3>
          <h3 className="singleMovieCastDiv">Released: {Released}</h3>
          <h3 className="singleMovieCastDiv">Country: {Country}</h3>
          <h3 className="singleMovieCastDiv">Genre: {Genre}</h3> */}
        </div>
        <div className="singleMovieTitleAndPosterDiv">
          <h1>{Title}</h1>
          <img src={Poster}></img>
        <div className="plotAndRatingsDiv">
          <div className="singleMoviePlotDiv">
            <h3>{Plot}</h3>
          </div>
          <div>
            <h3 className="singleMoviePlotDiv" style={{ display: "inline-block" }}>Ratings</h3>
          </div>
        </div>
        </div>
      </div>
      <Link to="/movies">
        <button className="singleMovieButton">Back to movies</button>
      </Link>
    </div>
  );
};

export default SingleMovie;
