import React from "react";
import Register from "../components/Register";
import { useState } from "react";
import { setValidatedUser } from "../store/validatedUser";
import { setPaginationCounter } from "../store/paginationCounter";
import { useDispatch, useSelector } from "react-redux";
import { Redirect } from 'react-router-dom'
import axios from "axios";

const RegisterContainer = () => {
  const { validatedUser } = useSelector((store) => store);
  const { registeredUser, SetRegisteredUser } = useState(false)

  const [user, setUser] = useState({
    name: "",
    lastname: "",
    username: "",
    email: "",
    password: "",
  });

  const dispatch = useDispatch();

  const changeHandler = (e) => {
    e.preventDefault();
    let value = e.target.value;
    let key = e.target.name;
    setUser({ ...user, [key]: value });
  };

  const blurHandler = (e) => {
    e.preventDefault();
    let value = e.target.value;
    let key = e.target.name;
    // console.log(value)
    // console.log(key)
    if (key === 'email') {
      console.log(key)
      if (!value.includes('@')) console.log('wrong email')
    }
    // setUser({ ...user, [key]: value });
  };

  // console.log("USER", user);

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .post("/api/register", user)
      .then((user) => {
        console.log("USERRRRR", user);
        SetRegisteredUser(true)
        // dispatch(
        //   setValidatedUser({ user: user.data.user, token: user.data.token })
        // );
        dispatch(setPaginationCounter(1));
        return <Redirect to="/"></Redirect>
      })
      .catch((err) => console.log("ERRRRR", err));
  };

  return (
    <div>
      <Register
        changeHandler={changeHandler}
        submitHandler={submitHandler}
        user={user}
        blurHandler={blurHandler}
        registeredUser={registeredUser}
      />
    </div>
  );
};

export default RegisterContainer;
