import React from "react";
import Login from "../components/Login";
import { useState } from "react";
import axios from "axios";
import { setValidatedUser } from "../store/validatedUser";
import { useDispatch, useSelector } from "react-redux";

const LoginContainer = () => {
  // const [user, setUser] = useState({ username: "", password: "" });

  let { validatedUser } = useSelector((state) => state);
  const dispatch = useDispatch();

  const changeHandler = (e) => {
    e.preventDefault();
    let value = e.target.value;
    let key = e.target.name;
    // setUser({ ...user, [key]: value });
    dispatch(setValidatedUser({ ...validatedUser, [key]: value }));
  };

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .post("/api/login", validatedUser)
      .then((user) => {
        console.log('USERRRR', user)
        dispatch(setValidatedUser({ ...validatedUser, token: user.data }));
      })
      .catch((err) => console.log("errrrrrr", err));
  };

  console.log('ASDASDASASDASSDDSA', validatedUser)

  return (
    <div>
      <Login changeHandler={changeHandler} submitHandler={submitHandler} />
    </div>
  );
};

export default LoginContainer;
