import React from 'react'
import '../index.css'

const MainSearch = () => {

    const formArray = ['Title...', 'Year...', 'Plot...']

    return (
        <div className='mainFormDiv'>
            <h1 style={{padding: "100px 10px 20px",margin:'0'}}>Search for a movie!</h1>
            <form>
                {formArray.map((item, index) => {
                    return <input type='text' name={item} placeholder={item} key={index} className='inputForm'></input>
                })}
                <button className='submitButton' style={{width: 'auto'}}>Submit</button>
            </form>
        </div>
        )
}

export default MainSearch
