import React from "react";
import isUserValidated from "../hooks/IsUserValidated";
import { useSelector } from "react-redux";
import { Redirect } from "react-router-dom";

const Register = ({ changeHandler, submitHandler, user, blurHandler }) => {
  const { validatedUser } = useSelector((state) => state);

  const isButtonDisabled = (user) =>
    !!Object.values(user).filter((item) => item.length < 2).length;

  const isValidated = isUserValidated(validatedUser);

  return !isValidated ? (
    <div className="registerMainDiv">
      {/* {isButtonDisabled(user) ? <h1>Please fill out all the fields</h1> : null} */}
      <form onSubmit={submitHandler} className="registerForm">
        {[
          "name",
          "lastname",
          "username",
          "email",
          "password",
          // "repeatPassword",
        ].map((item, index) => {
          return (
            <div key={index} className="inputAndErrorSubmittingDiv">
              <input
                onChange={changeHandler}
                onBlur={blurHandler}
                type={index < 4 ? "text" : "password"}
                required
                name={item}
                errorMessage='required'
                placeholder={
                  item
                  // !user[item].length ? (
                  //   "Please fill out this field"
                  // ) : `${item}`
                }
                key={index}
                className="registerFormInput"
              ></input>
            </div>
          );
        })}
        <button
          className="submitButton"
          style={{ width: "auto" }}
          disabled={isButtonDisabled(user)}
        >
          Submit
        </button>
      </form>
    </div>
  ) : (
    <Redirect to="/"></Redirect>
  );
};

export default Register;
