import React from "react";
import { Link } from "react-router-dom";
import { useSelector } from "react-redux";

const Movies = ({ ArraySlicer, clickHandler}) => {

  let { Search } = useSelector((state) => state.movies);

  let movies = ArraySlicer(Search);

  console.log('MOVIESSSSSS', movies)
  return (
    <div className="moviesAndButtonsDiv">
      <div className="moviesMainDiv">
        {movies &&
          movies.map((movies) => {
            return (
              <div className="movieByFiveDiv">
                {movies.map((movie) => {
                  return (
                    <div className="movieRowDiv">
                      <Link
                        className="styledLink"
                        to={`/movies/${movie.imdbID}`}
                      >
                        <h1>{movie.Title}</h1>
                        <img src={movie.Poster}></img>
                      </Link>
                    </div>
                  );
                })}
              </div>
            );
          })}
        {!movies.length ? (
          <div className="moviesAreEmptyMainDiv">
            <div className="moviesAreEmptyDiv">
              <h1>No movies loaded.</h1>
              <Link to="/">
                <button className="submitButton">Search For a Movie?</button>
              </Link>
            </div>
          </div>
        ) : null}
      </div>
      <div className='buttonsDiv'>
        <button onClick={clickHandler} name='nextPage' className="nextPageButtonDiv">{`>>`}</button>
        <button onClick={clickHandler} name='prevPage' className="nextPageButtonDiv">{`<<`}</button>
      </div>
    </div>
  );
};

export default Movies;
