import React from "react";
import Register from "../components/Register";
import { useState } from "react";
import { setValidatedUser } from "../store/validatedUser";
import { setPaginationCounter } from "../store/paginationCounter";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

const RegisterContainer = () => {
  const { validatedUser } = useSelector((store) => store);

  const [user, setUser] = useState({
    name: "",
    lastname: "",
    username: "",
    email: "",
    password: "",
  });

  const dispatch = useDispatch();

  const changeHandler = (e) => {
    // e.preventDefault();
    // let value = e.target.value;
    // let key = e.target.name;
    // setUser({ ...user, [key]: value });
    // console.log("hola!");
  };

  const blurHandler = (e) => {
    e.preventDefault();
    let value = e.target.value;
    let key = e.target.name;
    console.log(value)
    console.log(key)
    if (key === 'password') {
      console.log(key)
      if (!/\@/.test(value)) alert ('wrong password!')
    }
    setUser({ ...user, [key]: value });
  };

  console.log("USER", user);

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .post("/api/register", user)
      .then((user) => {
        console.log("USERRRRR", user);
        dispatch(
          setValidatedUser({ user: user.data.user, token: user.data.token })
        );
        dispatch(setPaginationCounter(1));
      })
      .catch((err) => console.log("ERRRRR", err));
  };

  return (
    <div>
      <Register
        changeHandler={changeHandler}
        submitHandler={submitHandler}
        user={user}
        blurHandler={blurHandler}
      />
    </div>
  );
};

export default RegisterContainer;
