import React from "react";
// import { Link } from 'react-router-dom'
import axios from "axios";
import SingleMovie from "../components/SingleMovie";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setSingleMovie } from "../store/singleMovie";

// https://www.omdbapi.com/?i=tt0096895&apikey=62e1844

const SingleMovieContainer = ({ match }) => {
  const { singleMovie } = useSelector((state) => state);
  const dispatch = useDispatch();
  const { id } = match.params;

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?i=${id}&apikey=62e1844`)
      .then((res) => res.data)
      .then((singleMovie) => {
        dispatch(setSingleMovie(singleMovie));
      });
  }, []);

  return <SingleMovie singleMovie={singleMovie}/>;
};

export default SingleMovieContainer;
