import React from "react";
import { useSelector, useDispatch } from "react-redux";
import { setPaginationCounter } from "../store/paginationCounter";
import PosterNotFound from "./PosterNotFound";
import ForwardAndBack from "./ForwardAndBack";
import { Link } from "react-router-dom";
import EmptyMovies from "./EmptyMovies";
import ArraySlicer from "../hooks/ArraySlicer";
import UserHasNoFavorites from "./UserHasNoFavorites";

const Favorites = () => {
  const { userFavorites, paginationCounter } = useSelector((store) => store);
  const dispatch = useDispatch();

  const movies = ArraySlicer(userFavorites);

  const clickHandler = (e) => {
    const { name } = e.target;
    dispatch(
      setPaginationCounter(
        name === "nextPage" ? paginationCounter + 1 : paginationCounter - 1
      )
    );
  };

  // userFavorites && console.log("USER FAVORITESSSSSasdads", userFavorites);

  return movies.length ? (
    <div className="moviesAndButtonsDiv">
      <div className="moviesMainDiv">
        {movies &&
          movies.map((movies) => {
            return (
              <div className="movieByFiveDiv">
                {movies.map((movie) => {
                  return (
                    <div className="movieRowDiv">
                      <Link
                        className="styledLink"
                        to={`/movies/${movie.imdbID}`}
                      >
                        <h1>{movie.Title}</h1>

                        {movie.Poster.length > 6 ? (
                          <div>
                            <div>
                              <img src={movie.Poster}></img>
                            </div>
                          </div>
                        ) : (
                          <div className="posterNotAvailableOnMoviesDiv">
                            <PosterNotFound />
                          </div>
                        )}
                      </Link>
                      <button>Delete</button>
                    </div>
                  );
                })}
              </div>
            );
          })}
        {!userFavorites.length ? <EmptyMovies movies={movies} /> : null}
      </div>
      <ForwardAndBack clickHandler={clickHandler} />
    </div>
  ) : (
    <UserHasNoFavorites />
  );
};

export default Favorites;
