import React from "react";
import Register from "../components/Register";
import { useState } from "react";
import { setValidatedUser } from "../store/validatedUser";
import { setPaginationCounter } from "../store/paginationCounter";
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";

const RegisterContainer = () => {

  const { validatedUser } = useSelector((store) => store)

  const [user, setUser] = useState({
    name: "",
    lastname: "",
    username: "",
    email: "",
    password: "",
  });

  const dispatch = useDispatch();

  const changeHandler = (e) => {
    e.preventDefault();
    let value = e.target.value;
    let key = e.target.name;
    setUser({ ...user, [key]: value });
  };

  console.log('USER', user)

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .post("/api/register", user)
      .then((user) => {
        console.log('USERRRRR', user)
        dispatch(setValidatedUser({user: user.data.user, token: user.data.token}));
        dispatch(setPaginationCounter(1))
      })
      .catch((err) => console.log("ERRRRR", err));
  };

  return (
    <div>
      <Register changeHandler={changeHandler} submitHandler={submitHandler} user={user}/>
    </div>
  );
};

export default RegisterContainer;
