import React from 'react'
import Users from '../components/Users'
import { useSelector, useDispatch } from 'react-redux'
import { setSearchUser } from '../store/searchUser'
import axios from 'axios'


const UsersContainer = () => {

    const { searchUser } = useSelector((store) => store)
    const dispatch = useDispatch()

    const changeHandler = (e) => {
        e.preventDefault()
        let key = e.target.name
        let { value } = e.target
        console.log(key, value)
        dispatch(setSearchUser({[key]: value}))
    }

    const submitHandler = (e) => {
        e.preventDefault()
        axios.get(`/api/users/${searchUser.username}`)
        .then((res) => console.log(res.data))

    }

    return (
        <Users changeHandler={changeHandler} submitHandler={submitHandler}/>
    )
}

export default UsersContainer
