import React from "react";
import MainSearch from "../components/MainSearch";
import { useState, useEffect } from "react";
import "../index.css";
import axios from "axios";

//EJEMPLOS
// https://www.omdbapi.com/?s=batman&type=series&apikey=62e1844 para buscar por las series de "batman"
// https://www.omdbapi.com/?s=batman&type=movie&apikey=62e1844 para buscar por las peliculas de "batman"
// https://www.omdbapi.com/?s=batman&type=series&y=2000&apikey=62e1844 para buscar por titulo, tipo y año

///Send all data requests to:

// http://www.omdbapi.com/?apikey=[yourkey]&

const MainSearchContainer = () => {
  const [search, setSearch] = useState({
    s: "",
    y: "",
    type: "",
  });
  const [movies, setMovies] = useState([]);

  const { s, y, type } = search;

  const [notSubmitButtonsArray, setNotSubmitButtonsArray] = useState([
    { name: "movie", isChecked: false },
    { name: "series", isChecked: false },
    { name: "episode", isChecked: false },
  ]);

  const changeHandler = (e) => {
    e.preventDefault();
    let key = e.target.name;
    let value = `${key}=${e.target.value.toLowerCase()}&`;
    setSearch({ ...search, [key]: value });
  };

  const clickHandler = (e) => {
    e.preventDefault();
    const value = `$type=${e.target.value.toLowerCase()}&`;
    setNotSubmitButtonsArray(notSubmitButtonsArray.map((button, index) => {
      console.log(button)
      return button.name === value
        ? {name: button.name, isChecked: true}
        : {name: button.name, isChecked: false};
    }));
    setSearch({ ...search, type: value });
  };

  const submitHandler = (e) => {
    e.preventDefault();
    axios
      .get(`https://www.omdbapi.com/?${s}${y}${type}apikey=62e1844`)
      .then((res) => {
        setMovies(res.data);
      });
  };

  console.log(movies);

  return (
    <MainSearch
      notSubmitButtonsArray={notSubmitButtonsArray}
      submitHandler={submitHandler}
      changeHandler={changeHandler}
      clickHandler={clickHandler}
    />
  );
};

export default MainSearchContainer;
