import React from 'react'
// import '../index.css'

const MainSearch = () => {

    const formArray = ['title', 'year', 'Plot']

    return (
        <div className='mainFormDiv'>
            <form>
                {formArray.map((item, index) => {
                    return <input className='inputForm' type='text' name={item} placeholder={item} key={index}></input>
                })}
            </form>
        </div>
        )
}

export default MainSearch
