import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { setMovies } from "../store/movies";

const Movies = ({ ArraySlicer }) => {
  let { Search } = useSelector((state) => state.movies);
  Search = ArraySlicer(Search);

  return (
    <div className="moviesMainDiv">
      {Search &&
        Search.map((movies) => {
          return (
            <div className="movieByFiveDiv">
              {movies.map((movie) => {
                return (
                  <div className="movieRowDiv">
                    <Link className="styledLink" to={`/movies/${movie.imdbID}`}>
                      <h1>{movie.Title}</h1>
                      <img src={movie.Poster}></img>
                    </Link>
                  </div>
                );
              })}
            </div>
          );
        })}
      <div className='moviesAreEmptyMainDiv'>
        {!Search.length ? (
          <h1 className="moviesAreEmptyDiv">No movies loaded.</h1>,
          <button>Search For a Movie?</button>
        ) : null}
      </div>
    </div>
  );
};

export default Movies;
