import React from "react";
import { Link } from "react-router-dom";
import { setMovies } from "../store/movies";
import axios from "axios";
import { useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setSingleMovie } from "../store/singleMovie";

export default ({ match }) => {
  const { singleMovie } = useSelector((state) => state);
  const dispatch = useDispatch();
  const { id } = match.params;

  useEffect(() => {
    axios
      .get(`https://www.omdbapi.com/?i=${id}&apikey=62e1844`)
      .then((res) => res.data)
      .then((singleMovie) => {
        dispatch(setSingleMovie(singleMovie));
      });
  }, []);

  const {
    Title,
    Year,
    Rated,
    Released,
    Director,
    Runtime,
    Ratings,
    Actors,
    Plot,
    Poster,
    Production,
    Writer,
  } = singleMovie;

  const array = [
    Title,
    Poster,
    Year,
    Director,
    Rated,
    Released,
    Runtime,
    Ratings,
    Actors,
    Plot,
    Production,
    Writer,
  ];

  console.log("ACTORSSS", Actors);

  return (
    <div className="singleMovieContainer">
      <h1>{Title}</h1>
      <img src={Poster}></img>
      <h2>Director: {Director}</h2>
      <h3>Cast: {Actors}</h3>
      <h3>{Plot}</h3>
    </div>
  );
};
