import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { useState, useEffect } from "react";
import { setValidatedUser } from "../store/validatedUser";
import axios from "axios";

const NavBar = () => {
  let { validatedUser } = useSelector((state) => state);

  const dispatch = useDispatch();

  useEffect(() => {
    axios
      .get("/api/auth")
      .then((res) => res.data)
      .then((user) => {
        if (user) dispatch(setValidatedUser({ ...user, isConnected: true }));
      })
      .catch((err) => console.log('ERRRORRRR', err));
  }, []);

  const clickHandler = () => {
    dispatch(setValidatedUser({ ...validatedUser, isConnected: false }));
  };

  return (
    <div>
      <nav className="footerDiv">
        <div className="footerFlexItems">
          {validatedUser.isConnected ? (
            <div className="homeAndLoginDiv">
              <Link to="/" className="footerBox">
                Home
              </Link>
              <Link to="/movies" className="footerBox">
                Movies
              </Link>
            </div>
          ) : null}
          <div>
            <Link onClick={() => clickHandler()} to="/users/login" className="footerBox">
              {!validatedUser.isConnected ? "Log in" : "log out"}
            </Link>
            <Link to="/users/register" className="footerBox">
              Register
            </Link>
          </div>
        </div>
      </nav>
    </div>
  );
};

export default NavBar;
