import React from 'react'
import '../index.css'

const MainSearch = ({submitHandler, changeHandler, clickHandler, notSubmitButtonsArray}) => {

    const formArray = ['Title...', 'Year...']
    const queries = ['s', 'y'] 

    return (
        <div className='mainFormDiv'>
            <h1 style={{padding: "40px 10px 20px",margin:'0'}}>Search for a movie!</h1>
            <form onSubmit={submitHandler}>
                {formArray.map((item, index) => {
                    return <input onChange={changeHandler} type='text' name={queries[index]} placeholder={item} key={index} className='inputForm'></input>
                })}
                <div className='notSubmitButtonsDiv'>
                {notSubmitButtonsArray.map(item => {
                   return <button key={item.name} className={`notSubmitButtonForm ${item.isChecked ? 'notSubmitButtonFocus' : ''}`} onClick={clickHandler} type="button" name={item.name}>{item.name}</button>
                })}
                {/* <button className='notSubmitButtonForm' onClick={clickHandler} type="button" name='series'>Series</button>
                <button className='notSubmitButtonForm' onClick={clickHandler} type="button" name='episode'>Episode</button> */}
                </div>
                <button className='submitButton' style={{width: 'auto'}}>Submit</button>
            </form>
        </div>
        )
}

//movie, series, episode

export default MainSearch
