import {
    createAction,
    createReducer,
  } from "@reduxjs/toolkit";

  export const setSearch = createAction("SEARCH");

  const searchReducer = createReducer(
    {},
    {
      [setSearch]: (state, action) => {
        return (state = action.payload);
      },
    }
  );

  export default searchReducer;
