import React from "react";
import { Link } from "react-router-dom";
import isUserValidated from "../hooks/IsUserValidated";
import { useDispatch, useSelector } from "react-redux";
import { Redirect } from "react-router-dom";

const Register = ({ changeHandler, submitHandler }) => {

  const { validatedUser } = useSelector((state) => state);

  const isValidated = isUserValidated(validatedUser);

  return !isValidated ? (
    <div className="registerMainDiv">
      <form onSubmit={submitHandler} className="registerForm">
        {[
          "name",
          "lastname",
          "username",
          "email",
          "password",
          // "repeatPassword",
        ].map((item, index) => {
          return (
            <input
              onChange={changeHandler}
              type={index < 4 ? "text" : "password"}
              name={item}
              placeholder={item !== "repeatPassword" ? item : "repeat password"}
              key={index}
              className="registerFormInput"
            ></input>
          );
        })}
        <button className="submitButton" style={{ width: "auto" }}>
          Submit
        </button>
      </form>
    </div>
  ) : <Redirect to='/'></Redirect>
};

export default Register;
