import React from "react";
import Favorites from "../components/Favorites";
import { useState, useEffect } from "react";
import { useSelector, validatedUser } from "react-redux";
import  setFavorites  from "../store/favorites";
import axios from "axios";

const FavoritesContainer = () => {
  const { validatedUser } = useSelector((store) => store);
  const { favorites } = useSelector((store) => store)

  const dispatch = useDispatch()

  const userId = validatedUser.data.user.id;

  useEffect(() => {
    axios
      .get(`/api/favorites/${userId}`)
      .then((userFavorites) => dispatch(setFavorites(userFavorites.data.favorites)));
  });

  return <Favorites />;
};

export default FavoritesContainer;
