import React from "react";
import ArrayPromisifier from "../hooks/ArrayPromisifier";
import { useEffect, useState } from "react";
import spinnerGifUserFavorites from "../assets/spinnerGifUserFavorites.gif";

const SingleUserInfo = ({ singleUser }) => {
  const [finalFavorites, setFinalFavorites] = useState([]);

  console.log(singleUser);

  const { favorites } = singleUser;

  let promis = [];

  useEffect(() => {
    // console.log('PROMISIFIERRR', ArrayPromisifier(favorites))
    ArrayPromisifier(favorites) && ArrayPromisifier(favorites).then((result) => setFinalFavorites(result));
  }, []);

  console.log("MOVIEEEEESSSSSSSS", finalFavorites);

  return (
    <div className="singleUserMainDiv">
      <h1 className="singleUserTitle">{singleUser.username}</h1>
      <h2>Favorite Movies:</h2>
      <div className="favoriteMoviesFromSingleUserDiv">
        {finalFavorites.length ? (
          finalFavorites.map((favorite) => {
            // console.log('FAVORITEEE', favorite)
            return (
              <h1 className="singleUserFavoriteMovieTitle" key={favorite.imdbID}>{favorite.Title}</h1>
            );
          })
        ) : (
            <div className="loadingBarDiv">
              {/* <img src={spinnerGifUserFavorites} alt="loading..." /> */}
              {/* <h1>No movies found!</h1> */}
            </div>
        )}
      </div>
    </div>
  );
};

export default SingleUserInfo;
